INSERT INTO `pm_subjects` VALUES ('295', '6739', '26', '完成成本的预测计算应包括：', 'EAC＝AC+（BAC-EV）', 'EAC＝BAC-CV', 'EAC＝AC+（BAC/CPI）', 'A或B', 'D', '详见挣值管理', '1', '2', '53', '0.00', '3959', '1833', '0');
