INSERT INTO `pm_subjects` VALUES ('302', '6746', '33', '项目经理确定一个项目存在与成本不一致的重复出现的问题，他非常担心管理层的看法。下面哪种类型的信息对项目经理评估绩效最有用', 'CPI', '问题日志', 'EAC', '项目预算', 'A', 'CPI是考量资源利用效率，即成本是否一致。', '1', '2', '53', '0.00', '3959', '2245', '0');
