INSERT INTO `pm_subjects` VALUES ('307', '6751', '38', '项目计划50天完工，任务一计划历时30天，在第40天完成，基于进度的绩效是多少，项目总工期是多少', '无法计算', '偏差为-10天，总工期50天', '绩效为0.75，预计总工期为50天', '绩效为0.75，预计总工期为67天', 'D', '挣值的延升，ES的概念，绩效等于30/40=0.75，预计总工期等于50/0.75=67天，详见圣略咨询杨波平PMBOK第六版讲义', '1', '2', '53', '0.00', '3959', '1740', '0');
