INSERT INTO `pm_subjects` VALUES ('406', '6530', '37', '在矩阵型组织中的项目规划阶段，项目经理决定需要获得人力资源。他需要从以下何人手中申请这些人员？', '项目经理', '职能部门经理', '项目团队', '项目出资人', 'B', '无', '1', '2', '55', '0.00', '3472', '2925', '0');
