INSERT INTO `pm_subjects` VALUES ('408', '6532', '39', '当多项目需要在职能组织结构中进行管理的时候，会出现很大的困难，因为:', '项目经理的权力级别', '不同项目在争取有限资源时的相对优先等级冲突', '项目团队成员关心他们各自的职能领域而不是项目本身', '项目经理需要利用处理人际关系的技能，以非正式的方法解决冲突', 'B', '多个项目，会去争抢资源', '1', '2', '55', '0.00', '3472', '1865', '0');
