INSERT INTO `pm_subjects` VALUES ('419', '6543', '50', '可以使用以下哪种结构来把组织中的部门与项目中的工作包联系起来？', '工作分解结构', '资源分解结构', '风险分解结构', '组织分解结构', 'D', '规划资源管理工具，OBS，详见PMBOK-P316', '1', '2', '55', '0.00', '3472', '1317', '0');
